const c=s=>{const e=document.querySelector("#notification");switch(e.children[0].innerHTML=s.title,e.children[1].innerHTML=s.description??"",e.classList.add("block"),e.classList.remove("hidden"),s.type){case"success":e.classList.add("bg-green-500");break;case"error":e.classList.add("bg-red-500");break}setTimeout(()=>{e.classList.remove("block"),e.classList.add("hidden")},3e3)};export{c};
